import app from "../../main.js"

const mutations = {
    SET_LANG(state, payload) {
        app.$i18n.locate.payload
    }
};

const actions = {
    setLang({comit}, payload) {
        comit('SET_LANG', payload)
    }
};

const language = {
    namespaced: true,
    actions,
    mutations
};

export default language;
