import {createI18n} from "vue-i18n";
import UserVi from "./admin/vi.json";
import UserEn from "./admin/en.json";
import RoleVi from "./admin/role.vi.json";
import RoleEn from "./admin/role.en.json";


const i18n = createI18n({
    legacy: false,
    locale: 'vi',
    fallbackLocale: 'vi',
    globalInjection: true,
    messages: {
        en: {...UserEn, ...RoleEn},
        vi: {...UserVi, ...RoleVi}
    }
})

export default i18n
