import {createI18n} from "vue-i18n";
import vnMessage from "./vi.json";
import enMessage from "./en.json";


const i18n = createI18n({
    legacy: false,
    locale: 'vi',
    fallbackLocale: 'vi',
    globalInjection: true,
    messages: {
        en: {...enMessage},
        vi: {...vnMessage}
    }
})

export default i18n
